"css`
    nav#bottom {
      position: fixed;
      bottom: -1px;
      left: 0;
      width: 100%;
      height: 50px;
      display: flex;
      background-color: #022f6f;
      align-items: center;
      text-align: center;
    }

    nav#bottom button {
      position: relative;
      flex: 1;
      height: 100%;
      color: transparent;
    }
    button {
      background-color: transparent;
      border: 0;
    }

    nav#bottom button.more:before {
      background-image: url(data:image/svg+xml;base64,);
    }
    nav#bottom button.add:before {
      background-image: url(data:image/svg+xml;base64,);
    }
    nav#bottom button.search:before {
      background-image: url(data:image/svg+xml;base64,);
    }
    nav#bottom button.delete:before {
      background-image: url(data:image/svg+xml;base64,);
    }
    nav#bottom button.save:before {
      background-image: url(data:image/svg+xml;base64,);
    }
    nav#bottom button:before {
      content: '';
      display: block;
      position: absolute;
      top: 50%;
      left: 50%;
      margin: 0;
      padding: 0;
      margin: calc(-32px / 2) 0 0 calc(-32px / 2);
      width: 32px;
      height: 32px;
      background: transparent no-repeat center;
    }
  `"

"css`
    header {
      position: fixed;
      width: 100%;
      height: 45px;
      top: 0;
      border-bottom: 2px solid #436bc7;
      background-color: #1c90fb;
      z-index: 10;
      transition: 0.5s;
    }

    header button.btn-back:before {
      content: '';
      display: block;
      width: 45px;
      height: 45px;
      background: transparent
        url(data:image/svg+xml;base64,)
        no-repeat 50%;
      transform: rotate(90deg);
    }

    header button.btn-back {
      width: 45px;
      height: 45px;
    }
    button {
      background-color: transparent;
      border: 0;
    }

    header h2.title {
      position: fixed;
      top: 0;
      left: 45px;
      width: calc(100% - 90px);
      height: 45px;
      line-height: 45px;
      background-color: transparent;
      text-align: left;
      color: #ffffff;
      font-size: 20px;
      z-index: 11;
      margin: 5px;
    }
  `"
